mapper.select:
select
  attributevalue_id, attributevalue_name, attributevalue_attributenameid, attributevalue_type, attributevalue_1, attributevalue_2, attributevalue_3
from wph.wph_attributevalue


        and attributevalue_id = #{attributevalueId}


        and attributevalue_name = #{attributevalueName}


        and attributevalue_attributenameid = #{attributevalueAttributenameid}


        and attributevalue_type = #{attributevalueType}


        and attributevalue_1 = #{attributevalue_1}


        and attributevalue_2 = #{attributevalue_2}


        and attributevalue_3 = #{attributevalue_3}
