mapper.select:
select
  middletype_id, middletype_name
from wph.wph_middletype


        and middletype_id = #{middletypeId}


        and middletype_name = #{middletypeName}


        and middletype_bigtyprid = #{middletypeBigtyprid}


        and middletype_type = #{middletypeType}


        and middletype_1 = #{middletype_1}


        and middletype_2 = #{middletype_2}


        and middletype_3 = #{middletype_3}
